import matplotlib.pyplot as plt
import numpy as np

# Configurações básicas
periodo_clock = 8  # Período do clock em nanosegundos
num_ciclos = 75   # Número de ciclos do clock

# Tempo total em nanosegundos
tempo_total = periodo_clock * num_ciclos
tempo = np.linspace(0, tempo_total, num_ciclos * 2)

# Função para gerar sinais
def gerar_sinal_clock(periodo, num_ciclos):
    sinal = []
    for _ in range(num_ciclos):
        sinal.extend([1, 0])
    return sinal

def gerar_sinal(transicoes, num_ciclos, nivel_inicial=0):
    sinal = [nivel_inicial]
    for t in transicoes:
        sinal.extend([sinal[-1]] * t)
        sinal.append(1 - sinal[-1])
    sinal.extend([sinal[-1]] * (num_ciclos * 2 - len(sinal)))
    return sinal[:num_ciclos * 2]

# Sinais a partir da imagem
sinal_clock = gerar_sinal_clock(periodo_clock, num_ciclos)

transicoes_req = [10, 20, 50]  # Exemplos de transições do sinal sinal_requisicao
sinal_requisicao = gerar_sinal(transicoes_req, num_ciclos, 1)

transicoes_gnt = [30, 40]  # Exemplos de transições do sinal sinal_concessao
sinal_concessao = gerar_sinal(transicoes_gnt, num_ciclos, 0)

transicoes_frame = [15, 35, 55]  # Exemplos de transições do sinal sinal_quadro
sinal_quadro = gerar_sinal(transicoes_frame, num_ciclos, 0)

transicoes_ad = [20, 45, 70]  # Exemplos de transições do sinal sinal_endereco_dados
sinal_endereco_dados = gerar_sinal(transicoes_ad, num_ciclos, 1)

transicoes_irdy = [25, 60]  # Exemplos de transições do sinal sinal_iniciador_pronto
sinal_iniciador_pronto = gerar_sinal(transicoes_irdy, num_ciclos, 1)

transicoes_trdy = [35, 65]  # Exemplos de transições do sinal sinal_alvo_pronto
sinal_alvo_pronto = gerar_sinal(transicoes_trdy, num_ciclos, 1)

# Plotando as formas de onda
fig, axs = plt.subplots(7, 1, sharex=True, figsize=(12, 8))

sinais = [sinal_clock, sinal_requisicao, sinal_concessao, sinal_quadro, sinal_endereco_dados, sinal_iniciador_pronto, sinal_alvo_pronto]
rotulos = ['sinal_clock', 'sinal_requisicao', 'sinal_concessao', 'sinal_quadro', 'sinal_endereco_dados', 'sinal_iniciador_pronto', 'sinal_alvo_pronto']

for i, sinal in enumerate(sinais):
    axs[i].step(tempo, sinal, where='post')
    axs[i].set_ylabel(rotulos[i], rotation=0, labelpad=40, va='center', ha='right')
    axs[i].set_yticks([0, 1])
    axs[i].grid(True)

axs[-1].set_xlabel('Tempo (ns)')
plt.tight_layout()
plt.show()

